// ANIMATIONS

$(document).ready(function(){
	$("#twitterFeed").delay(5000).animate({bottom:'5'}, 3000, 'easeOutElastic');
	$("#album").delay(2500).animate({left:'0'}, 4000, 'easeOutElastic');
		
});

$(document).ready(function() {
 
	// Expand Panel
	$("#open").click(function(){
		$("div#photos").animate({right:'-65'}, 2500, 'easeOutElastic');
		$("div#photos").css({'z-index' : '100'})
		return false;
	});	
 
	// Collapse Panel
	$("#close").click(function(){
		$("div#photos").animate({right:'-284'}, 2500, 'easeOutElastic');
		$("div#photos").css({'z-index' : '20'})
		return false;
	});		
 
	// Switch buttons from "Log In | Register" to "Close Panel" on click
	$("#toggle a").click(function () {
		$("#toggle a").toggle();
		return false;
	});		
 
});

$(document).ready(function() {
 
	// Expand Panel
	$("#openTwo").click(function(){
		$("div#videos").animate({right:'-65'}, 2000, 'easeOutElastic');
		$("div#videos").css({'z-index' : '100'})
		return false;
	});	
 	
	// Collapse Panel
	$("#closeTwo").click(function(){
		$("div#videos").animate({right:'-384'}, 2000, 'easeOutElastic');
		$("div#videos").css({'z-index' : '20'})
		return false;
	});		
 
	// Switch buttons from "Log In | Register" to "Close Panel" on click
	$("#toggleTwo a").click(function () {
		$("#toggleTwo a").toggle();
		return false;
	});		
 
});

